Item.value:
// Function to convert text to Proper Case
function toProperCase(text) {
    return text.replace(/\b\w+/g, function(word) {
        return word.charAt(0).toUpperCase() + word.slice(1).toLowerCase();
    });
}

// Recursive function to convert bookmark titles to Proper Case
function convertBookmarksToProper(bm) {
    if (!bm) return;
    if (bm.name && bm.name.trim().toUpperCase() === "S&P GLOBAL COVER PAGE") {
        bm.name = "S&P Global Cover Page";
    } else if (bm.name) {
        bm.name = toProperCase(bm.name);
    }
    if (bm.children && bm.children.length > 0) {
        for (var i = 0; i < bm.children.length; i++) {
            convertBookmarksToProper(bm.children[i]);
        }
    }
}

// Start from the root bookmark
var root = this.bookmarkRoot;
if (root && root.children && root.children.length > 0) {
    for (var i = 0; i < root.children.length; i++) {
        convertBookmarksToProper(root.children[i]);
    }
    app.alert("All bookmark titles have been converted to Proper Case. 'S&P GLOBAL COVER PAGE' is now 'S&P Global Cover Page'.");
} else {
    app.alert("No bookmarks found in this document.");
}
