// Recursive function to convert bookmark titles to uppercase
function convertBookmarksToUpper(bm) {
    if (!bm) return;
    bm.name = bm.name.toUpperCase();
    if (bm.children && bm.children.length > 0) {
        for (var i = 0; i < bm.children.length; i++) {
            convertBookmarksToUpper(bm.children[i]);
        }
    }
}

// Start from the root bookmark
var root = this.bookmarkRoot;
if (root && root.children && root.children.length > 0) {
    for (var i = 0; i < root.children.length; i++) {
        convertBookmarksToUpper(root.children[i]);
    }
    app.alert("All bookmark titles have been converted to CAPITAL LETTERS.");
} else {
    app.alert("No bookmarks found in this document.");
}
