// Function to search for today's date in various formats on the first page
function findTodaysDate() {
    // Get the current date
    var today = new Date();
    var month = today.getMonth() + 1; // Months are zero-based
    var day = today.getDate();
    var year = today.getFullYear();

    // Format the date as mm dd yyyy
    var formattedDateWithSpaces = (month < 10 ? '0' : '') + month + ' ' +
                                  (day < 10 ? '0' : '') + day + ' ' + year;

    // Initialize a variable to store the text of the first page
    var pageText = '';

    // Loop through all words on the first page to construct the text
    var numWords = this.getPageNumWords(0);
    for (var i = 0; i < numWords; i++) {
        pageText += this.getPageNthWord(0, i) + ' ';
    }

    // Ensure pageText is a string
    pageText = pageText.toString();

    // Regular expression to match the date format with spaces
    var dateRegex = new RegExp('\\b' + formattedDateWithSpaces + '\\b');

    // Check if the formatted date is present in the text of the first page
    if (dateRegex.test(pageText)) {
        app.alert({
            cMsg: "Today's date (" + formattedDateWithSpaces + ") is found on the first page.",
            cTitle: "Date Found",
            nIcon: 3, // Information icon
            nType: 0  // OK button
        });
    } else {
        app.alert({
            cMsg: "Today's date (" + formattedDateWithSpaces + ") is not found on the first page.",
            cTitle: "Date Not Found",
            nIcon: 0, // Warning icon
            nType: 0  // OK button
        });
    }
}

// Execute the function
findTodaysDate();
